<?php/** * The admin-specific functionality of the plugin. * * @link       https://www.dukeyin.com/corona-virus-data/ * @since      1.1.1 * * @package    Corona_Virus_Data * @subpackage Corona_Virus_Data/admin *//** * The admin-specific functionality of the plugin. * * Defines the plugin name, version, and two examples hooks for how to * enqueue the admin-specific stylesheet and JavaScript. * * @package    Corona_Virus_Data * @subpackage Corona_Virus_Data/admin * @author     Duke Yin <duke@dukeyin.com> */class Corona_Virus_Data_Admin {	/**	 * The ID of this plugin.	 *	 * @since    1.0.0	 * @access   private	 * @var      string    $plugin_name    The ID of this plugin.	 */	private $plugin_name;	/**	 * The version of this plugin.	 *	 * @since    1.0.0	 * @access   private	 * @var      string    $version    The current version of this plugin.	 */	private $version;	/**	 * Initialize the class and set its properties.	 *	 * @since    1.0.0	 * @param      string    $plugin_name       The name of this plugin.	 * @param      string    $version    The version of this plugin.	 */	public function __construct( $plugin_name, $version ) {		$this->plugin_name = $plugin_name;		$this->version = $version;			}	/**	 * Register the stylesheets for the admin area.	 *	 * @since    1.0.0	 */	public function enqueue_styles() {		/**		 * This function is provided for demonstration purposes only.		 *		 * An instance of this class should be passed to the run() function		 * defined in Corona_Virus_Data_Loader as all of the hooks are defined		 * in that particular class.		 *		 * The Corona_Virus_Data_Loader will then create the relationship		 * between the defined hooks and the functions defined in this		 * class.		 */		wp_enqueue_style( $this->plugin_name, plugin_dir_url( __FILE__ ) . 'css/corona-virus-data-admin.css', array(), $this->version, 'all' );	}	/**	 * Register the JavaScript for the admin area.	 *	 * @since    1.0.0	 */	public function enqueue_scripts() {		/**		 * This function is provided for demonstration purposes only.		 *		 * An instance of this class should be passed to the run() function		 * defined in Corona_Virus_Data_Loader as all of the hooks are defined		 * in that particular class.		 *		 * The Corona Virus Data_Loader will then create the relationship		 * between the defined hooks and the functions defined in this		 * class.		 */		wp_enqueue_script( $this->plugin_name, plugin_dir_url( __FILE__ ) . 'js/corona-virus-data-admin.js', array( 'jquery' ), $this->version, false );	}}///Admin Menuadd_action( 'admin_menu', 'cov__add_admin_menu' );add_action( 'admin_init', 'cov__settings_init' );function cov__add_admin_menu() {	$covd_title = __( 'Corona Virus Data', 'corona-virus-data' );	add_options_page( $covd_title, $covd_title, 'manage_options', 'corona-virus-data', 'cov__options_page' );}function cov__settings_init() { 	register_setting( 'pluginPage', 'cov__settings' );	add_settings_section(		'cov__pluginPage_section', 		__( 'Country or region to show', 'corona-virus-data' ), 		'cov__settings_section_callback', 		'pluginPage'	);	add_settings_field( 		'cov__select_field_0', 		__( 'Select country or region', 'corona-virus-data' ), 		'cov__select_field_0_render', 		'pluginPage', 		'cov__pluginPage_section' 	);}function cov__select_field_0_render() { 	$options = get_option( 'cov__settings' );	?><style type="text/css">#world{width:84%;height:auto}@media screen and (max-width: 782px){#world{width:100%;height:auto}}</style>	<select name='cov__settings[cov__select_field_0]' id="country_name">	<option value="CN" <?php selected( $options['cov__select_field_0'], 'CN' ); ?>><?php _e('China','corona-virus-data'); ?></option>	<option value="IT" <?php selected( $options['cov__s