.querySelector("[cc=<?php echo strtolower(get_option( 'cov__settings' )['cov__select_field_0']) ?>]").style.fill = "#fe6553";         doc.onclick = ({ target }) => {          var g = target.closest('path'); 		  if (!g) return;              cc = target.getAttribute("cc") || target.parentElement.getAttribute("cc");              na = target.getAttribute("na") || target.parentElement.getAttribute("na");  [...elements].forEach(e => e.classList.remove('selected'));  g.classList.add('selected');          if (cc) {            document.getElementById("country_code").value = cc.toUpperCase();            document.getElementById("country_name").value = cc.toUpperCase();          }        };      });    }());  </script>			<?php			settings_fields( 'pluginPage' );			do_settings_sections( 'pluginPage' );			submit_button();			?>		</form>		<?php}